toolSchema:
{
  "type": "object",
  "properties": {
    "name": {
      "type": "string",
      "description": "Name of the person to greet"
    },
    "language": {
      "type": "string",
      "enum": ["english", "spanish", "french"],
      "description": "Language for the greeting"
    },
    "formal": {
      "type": "boolean",
      "description": "Whether to use formal greeting"
    }
  },
  "required": ["name"]
}

func:
const args = msg.payload.arguments;
const { operation, a, b } = args;

let result;
switch (operation) {
    case 'add':
        result = a + b +1;
        break;
    case 'subtract':
        result = a - b;
        break;
    case 'multiply':
        result = a * b;
        break;
    case 'divide':
        if (b === 0) {
            return {
                topic: 'mcp-tool-response',
                payload: {
                    executionId: msg.payload.executionId,
                    error: 'Division by zero is not allowed'
                }
            };
        }
        result = a / b;
        break;
    default:
        return {
            topic: 'mcp-tool-response',
            payload: {
                executionId: msg.payload.executionId,
                error: 'Unknown operation: ' + operation
            }
        };
}
let final_result = {"type": "text", "text": `${a} ${operation} ${b} = ${result}`}
return {
    topic: 'mcp-tool-response',
    payload: {
        executionId: msg.payload.executionId,
        result: {
            content:[final_result]
        }
    }
};

const args = msg.payload.arguments;
const { name, language = 'english', formal = false } = args;

const greetings = {
    english: {
        formal: `Good day, ${name}. It is a pleasure to meet you.`,
        informal: `Hello ${name}! Nice to meet you!`
    },
    spanish: {
        formal: `Buenos días, ${name}. Es un placer conocerle.`,
        informal: `¡Hola ${name}! ¡Encantado de conocerte!`
    },
    french: {
        formal: `Bonjour ${name}. C'est un plaisir de vous rencontrer.`,
        informal: `Salut ${name} ! Ravi de te rencontrer !`
    }
};

const greeting = greetings[language] 
    ? greetings[language][formal ? 'formal' : 'informal']
    : greetings.english[formal ? 'formal' : 'informal'];

let final_result = {"type": "text", "text": greeting}
return {
    topic: 'mcp-tool-response',
    payload: {
        executionId: msg.payload.executionId,
        result: {
            greeting: greeting,
            name: name,
            language: language,
            formal: formal,
            timestamp: new Date().toISOString(),
            content:[final_result] // 必须要这样写
        }
    }
};
